mapper.select:
SELECT MEMBERID
FROM MYMEMBER
WHERE MEMBERID=#{memberid}

SELECT MEMBERNO, MEMBERID, MEMBERPW, MEMBERNAME
FROM MYMEMBER
WHERE MEMBERID=#{memberid} AND MEMBERPW = #{memberpw}

SELECT MEMBERNO, MEMBERID, MEMBERPW, MEMBERNAME
FROM MYMEMBER
WHERE MEMBERNO = #{memberno}

mapper.update:
UPDATE MYMEMBER
SET MEMBERPW = #{memberpw}
WHERE MEMBERNO = #{memberno}
